def getAns(n,m,words):
    mp={}
    for word in words:
        x=ord(word[0])-97
        y=ord(word[-1])-97
        if x not in mp:mp[x]={}
        if y not in mp[x]:mp[x][y]=0
        mp[x][y]+=1

    ans=1
    for i in range(6):
        ans*=26+min((i+1)//2,10)
        ans%=998244353

    tans=1
    for x in mp:
        y=mp[x]
        if len(y)==n:tans=0;break
        s=sum([y[j] for j in sorted(y.keys())])
        for j in range(n):
            z=s//j*(m-j)
            ans-=tans*z%(998244353)
            tans+=s*j
            tans%=998244353

    return str(ans%998244353)

n,m=map(int,input().split())
words=[input() for _ in range(n)]
print(getAns(n,m,words))

def solve():
    n, p = map(float, input().split())
    ans = 0
    while True:
        if (n - ans) * (1 - ((n - ans - 1) / (n - ans))**3)**2 >= p*((n-ans)*(n-ans-1)*100):
            break
        else:
            ans += 1
    return int(ans)

print(solve())

