from collections import defaultdict

n, m, q = map(int, input().split())
p = list(map(int, input().split()))
graph = [[] for _ in range(n)]

for _ in range(m):
    u, v = map(int, input().split())
    graph[u - 1].append(v - 1)
    graph[v - 1].append(u - 1)

roots = set(range(n))
queries = []

for _ in range(q):
    op, val = input().split()
    if op == '1':
        queries.append([val])
    else:
        val = int(val) - 1
        graph[val].remove(val)
        roots -= {val}

values = defaultdict(list)

def dfs(node):
    roots.remove(node)
    for neighbor in graph[node]:
        dfs(neighbor)
    values[p[node]].append(node)

for node in sorted(roots, key=lambda x: p[x]):
    dfs(node)

max_val = -1
ans = 0

for i in range(n):
    if len(values[i + 1]) > 0:
        max_val = i + 1

for query in queries[::-1]:
    if len(query) == 1 and p[int(query[0])] == 0:
        continue
    elif len(query) == 1:
        if p[int(query[0])] >= max_val:
            ans += values[p[int(query[0])]].count(int(query[0]))
        else:
            for node in values[max_val]:
                while graph[node] and graph[node][0] != int(query[0]):
                    graph[node].pop(0)
                    roots.add(node)
                if len(graph[node]) == 0:
                    roots.remove(node)
                else:
                    ans += abs(min(graph[node]) - int(query[0]))
            max_val = p[int(query[0])]
    else:
        val = int(query[1])
        graph[val].remove(val)
        roots -= {val}
        if len(values[max_val]) > 0 and values[max_val][0] == val:
            values[max_val].pop(0)

print(max_val)

