import sys

def read_ints():
    return list(map(int, input().split()))

def solve(n, x, pos):
    MOD = int(1e9) + 7
    inv_n = pow(n, MOD-2, MOD)
    fact = [0] * (n+1)
    fact[0], fact[1] = 1, 1
    for i in range(2, n+1):
        fact[i] = (fact[i-1] * i) % MOD

    ans = fact[pos]
    if x > pos:
        ans *= pow(n-pos, MOD-2, MOD)
        ans %= MOD

    return ans

t = 1
# t = int(input())
