import sys

input = lambda : sys.stdin.readline().strip()
ints = lambda : list(map(int, input().split()))

def solve():
    n, m, mod = ints()
    if m == 0:
        return print(0)

    inv = pow(n + 1, mod - 2, mod)
    ways = [1]
    for i in range(m):
        ways.append((ways[-1] * (n + 1)) % mod)
    ways_inv = [pow(x, mod - 2, mod) for x in ways]

    dp = [[[0 for _ in range(n)] for _ in range(n + 1)] for _ in range(m + 1)]
    for i in range(m):
        for j in range(1, n + 1):
            for k in range(j - 1, n + 1):
                if j == k:
                    dp[i][j][k] = 0
                else:
                    dp[i][j][k] = (dp[i][j][k] + dp[i][j][min(k, min(n, max(0, i - abs(j - k) + 1)))] * (ways_inv[i] if j == n else 0)) % mod
                for l in range(j + 1, min(i + 2, n + 1)):
                    dp[i + 1][l][max(k, l)] = (dp[i + 1][l][max(k, l)] +
                                                dp[i][j][k] * ways_inv[i] * abs(l - max(k, l)) % mod) % mod

    print(sum(dp[m]) % mod)

if __name__ == "__main__":
    solve()

