import math

def g(x, p):
    cnt = 0
    while x % p == 0:
        x //= p
        cnt += 1
    return cnt

def f(x, y):
    ans = 1
    for p in prime[x]:
        cnt = g(y, p)
        if cnt > 0:
            ans *= (pow(p, cnt, 10**9 + 7))
            ans %= 10**9 + 7
    return ans

def prime(x):
    ret = []
    i = 2
    while x > 1 and i * i <= x:
        if x % i == 0:
            ret.append(i)
            while x % i == 0:
                x //= i
        i += 1
    if x > 1:
        ret.append(x)
    return ret

