from collections import defaultdict

def getAns(m):
    n = len(m)
    ans = 0
    m.sort()
    colnum = defaultdict(int)
    colnumcnt = defaultdict(lambda: [0, 0])
    numcol = defaultdict(list)
    for i in range(n):
        temp = set()
        j = 1
        while True:
            if i + j < n and m[i + j] == m[i]:
                break
            if i - j >= 0 and m[i - j] == m[i]:
                colnum[m[i]] += 1
                colnumcnt[m[i]][0] += 1
                numcol[m[i]].append(i)
                temp.add(i - j)
                ans += abs(i - (i - j))
            if i + j < n and m[i + j] == m[i]:
                temp.add(i + j)
            j += 1
        for key in temp:
            colnumcnt[m[key]][1] += 1
    q = [[0, 0]]
    for i in range(n):
        while True:
            if len(q) > 0 and abs(q[-1][0] - i) <= colnum[m[i]]:
                ans += colnumcnt[m[i]][0]
                break
            elif len(numcol[m[i]]) == colnumcnt[m[i]][1]:
                q.pop()
                continue
            else:
                x = numcol[m[i]].pop()
                if abs(x - i) < abs(q[-1][0] - i):
                    ans += abs(i - x)
                else:
                    while True:
                        ans += abs(i - q[-1][0])
                        del q[-1]
                        if len(q) == 0 or abs(q[-1][0] - i) <= colnum[m[i]]:
                            break
        q.append([i, colnum[m[i]]])

    print("YES" if ans > 0 else "NO")

