def solve(n, k):
    if n == 1:
        return 0
    mod = (10**9) + 7

    dp = [[0]*(n+2) for _ in range(n+2)]
    for i in range(1, n+2):
        dp[i][i] = 1

    for block_length in range(2, n//k + 2):
        dp[1][block_length] = ((pow(block_length - 1, k, mod)) * (block_length - 1)) % mod
        for i in range(2, n-block_length+2):
            prev_blocks = n - (i-1)*block_length

            if prev_blocks >= block_length:
                dp[i][block_length] += (prev_blocks//block_length) * dp[i-1][block_length]
                dp[i][block_length] %= mod

                dp[i][block_length] += ((n-i*block_length)//(block_length-1)) * dp[i+1][block_length - 1]
                dp[i][block_length] %= mod
            else:
                dp[i][block_length] = (prev_blocks//block_length) * dp[i-1][block_length]
                dp[i][block_length] %= mod

    return (dp[1][n]) % mod

