from collections import defaultdict
import sys

sys.setrecursionlimit(10**7)
input = lambda: sys.stdin.readline().rstrip()

class UnionFind:
    def __init__(self, n):
        self.parent = list(range(n))
        self.size = [1] * n

    def find(self, a):
        acopy = a
        while a != self.parent[a]:
            a = self.parent[a]
        while acopy != a:
            self.parent[acopy], acopy = a, self.parent[acopy]
        return a

    def union(self, a, b):
        pa, pb = self.find(a), self.find(b)
        if pa == pb: return
        if self.size[pa] > self.size[pb]:
            self.parent[pb] = pa
            self.size[pa] += self.size[pb]
        else:
            self.parent[pa] = pb
            self.size[pb] += self.size[pa]

def main():
    t = int(input())
    allans = []
    for _ in range(t):
        n, k = map(int, input().split())
        A = list(map(int, input().split()))
        edges = [tuple(map(int, input().split())) for _ in range(n-1)]
        uf = UnionFind(n)
        colnum = defaultdict(set)
        numcol = {}
        colnumcnt = defaultdict(lambda: 0)
        q = [[] for __ in range(n)]

        for u, v in edges:
            pu, pv = uf.find(u), uf.find(v)
            if pu != pv:
                uf.union(pu, pv)
                A[pu] = max(A[pu], A[pv])
            else:
                colnumcnt[u] += 1
                q[numcol[v]].append(u)
        for i in range(n):
            numcol[i] = uf.find(i)
            A[i] = max(A[i], A[numcol[i]])
            if colnumcnt[i]:
                colnum[numcol[i]].add(i)

        ans = 0
        for i, a in enumerate(sorted(enumerate(A), key=lambda x: -x[1])):
