def count_codes(hint):
    n = len(hint)
    if '?' not in hint:
        return 1

    count = [inf] * (10 ** (n - hint.count('?')))

    i = 0
    prev_val = '?'
    seen = set()
    for char in hint:
        if char == '?':
            pass
        else:
            if prev_val != '?' and char < prev_val:
                return 0
            elif prev_val != '?' and char > prev_val:
                count[i] = 1
            elif prev_val != '?':
                count[i] = 0

            for j in range(i, len(count)):
                count[j] *= 10 ** (int(char) - int(prev_val))

            i += 1
            prev_val = char

    ans = 0
    for x in count:
        if x != inf:
            if hint.count('A') <= len(seen):
                ans += x
            elif 'A' not in seen:
                ans += x * (10 ** (hint.count('A')))

            prev_len = len(seen)
            seen.add('A')
    return ans

