def min_change_places(n, m, dressing_room):
    sight_levels = [int(x) for x in dressing_room[0].split()]
    inconvenience_counter = 0
    sorted_sight_levels = sorted(sight_levels)

    colnum = {}
    colnumcnt = {}
    numcol = {}

    for i, level in enumerate(sorted_sight_levels):
        if not (level in colnum.keys()):
            colnum[level] = i
            colnumcnt[level] = 0
            numcol[i] = level

    q = [[] for _ in range(n)]

    for level in sorted_sight_levels:
        temp_rows = set()
        current_index = colnum[level]

        if (current_index + m) >= len(sorted_sight_levels):
            rows_to_fill = [i for i, x in enumerate(q)]
            start_index = n - 1
            end_index = n

        else:
            rows_to_fill = []
            temp_rows.add(current_index % n)
            start_index = current_index
            end_index = min(current_index + m + 1, len(sorted_sight_levels))

        for i in range(start_index, end_index):
            if numcol[i] == level and i not in temp_rows:
                q[i // m].append(i)

        while (start_index >= current_index) and start_index < end_index:
            row_to_fill = rows_to_fill.pop(0)
            while len(q[row_to_fill]) > 1:
                index = sorted_sight_levels.index(numcol[start_index])
                q[row_to_fill].sort(key=lambda x: abs(x - index))

                current_row = q[row_to_fill].pop()
                temp_rows.add(current_row)

                if current_row == index and colnumcnt[level] < len(q):
                    colnumcnt[level] += 1
                    numcol[len(q)] = level

                current_index += 1

        rows_needed = end_index - start_index
        rows_available = n - len(temp_rows)
        rows_missing = max(0, rows_needed - rows_available)

        for _ in range(rows_missing):
            temp_rows.add(len(q))
            inconvenience_counter += abs(numcol[start_index] - level)

    return str(inconvenience_counter)


