def maxOnes(n, arr):
    ones = sum(arr)

    if ones == n:
        return str(ones)

    for i in range(n - 1):
        left_ones = sum(arr[:i + 1])
        right_ones = n - left_ones

        if left_ones >= right_ones and ones < n:
            return str(n - (n - ones))

        elif left_ones > right_ones:
            return str(ones)

    return str(max(ones, n - ones))

