        if x not in colnum:
            colnum[x] = []
            colnumcnt[x] = 1
            numcol[y] = [x]
        else:
            colnum[x].append(y)
            colnumcnt[x] += 1
            numcol[y].append(x)

    for i, (x, y) in enumerate(m):
