import sys
from collections import defaultdict, deque

input = sys.stdin.readline

def read_list():
    return list(map(int, input().split()))

def read_int():
    return int(input())

def read_ints():
    return map(int, input().split())

def solve():
    n, m = read_ints()
    A = read_list()

    ans = 0
    A.sort()

    colnum = {}
    numcol = defaultdict(list)
    colnumcnt = defaultdict(int)

    for i, a in enumerate(A):
        if a not in colnum:
            colnum[a] = len(colnum) + 1
            numcol[colnum[a]].append(i)
            colnumcnt[colnum[a]] += 1

    q = [[] for _ in range(n+1)]

    for i, a in enumerate(A):
        temp = [j for j in numcol[colnum[a]] if j > i]
        for j in temp:
            while len(q[j]) < m and A[q[j][-1]] != a:
                q[j].append(i)

    for i, a in enumerate(A):
        temp = [j for j in numcol[colnum[a]] if j > i]
        for j in temp:
            while colnumcnt[colnum[a]] == len(q[j]) and A[q[j][-1]] != a:
                q[j].append(i)
                colnumcnt[colnum[a]] -= 1

        ans += sum((j - i) // m * (m + 1) // 2 for j in numcol[colnum[a]])

    print(ans)

