def transform_email():
    s = input().strip()

    if not s.startswith('at') and not s.endswith('at'):
        return 'a' + s.replace('dot', '.').replace('at', '@')

    if len(s) == 3:
        return 'a@t'

    i = s.find('@')
    j = s.rfind('dot')

    if i != j - 3:
        s = s[:i] + 'dot' + s[i:]
        if j < i:
            s += '@' + s[j + 4:]
        else:
            s = s[:j] + s[j + 4:i] + 'dot' + s[i:]

    return s

print(transform_email())

